package Learn0;

public class _7_PlusMinusSymbols {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//++a first increment
		//a++ increment later
		
		//--a first decrement
		//a-- decrement later
		
		int number = -2;
		number = +2;
		number++;
		
		System.out.println(number);
		System.out.println(++number);
		
		number++;
		
		System.out.println(number);

		System.out.println(-number);
		
	}

}
